import User from '../models/user.model.js';
import cloudinary from '../config/cloudinary.config.js';

export const createUser = async (req, res, next) => {
  try {
    const requiredFields = [
      'country', 'city', 'course', 'proficiency',
      'fullName', 'fatherName', 'email', 'cnic',
      'phone', 'dob', 'gender', 'qualification', 'hasLaptop'
    ];

    for (const field of requiredFields) {
      if (!req.body[field]) {
        return res.status(400).json({
          success: false,
          message: `${field} is required`
        });
      }
    }

    if (!req.file) {
      return res.status(400).json({
        success: false,
        message: 'Image is required'
      });
    }

    const imageResult = await new Promise((resolve, reject) => {
      const uploadStream = cloudinary.uploader.upload_stream(
        { folder: 'user_uploads' },
        (error, result) => {
          if (error) return reject(error);
          resolve(result);
        }
      );
      uploadStream.end(req.file.buffer);
    });

    const newUser = new User({
      ...req.body,
      fatherNic: req.body.fatherCnic || null,
      imageUrl: imageResult.secure_url,
      imagePublicId: imageResult.public_id
    });

    await newUser.save();

    return res.status(201).json({
      success: true,
      message: 'User created successfully',
      user: newUser
    });

  } catch (error) {
    next(error);
  }
};

export const getUserByCnic = async (req, res, next) => {
  try {
    const user = await User.findOne({ cnic: req.params.cnic });
    if (!user) {
      return res.status(404).json({ 
        success: false, 
        message: "User not found" 
      });
    }
    return res.json({ success: true, user });
  } catch (error) {
    next(error);
  }
};
