import React from 'react';
import Button from 'react-bootstrap/Button';

const CustomButton = ({ text, active }) => {
  return (
    <Button
      variant="light" // white background
      style={{
        color: active ? 'green' : 'blue',
        border: '1px solid blue',
        fontWeight: 'bold',
      }}
      active={active}
    >
      {text}
    </Button>
  );
};

export default CustomButton;
