// ✅ Frontend: React + Vite (Saylani Form)
import React, { useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [formData, setFormData] = useState({
    country: "",
    city: "",
    course: "",
    proficiency: "",
    fullName: "",
    fatherName: "",
    email: "",
    phone: "",
    cnic: "",
    fatherCnic: "",
    dob: "",
    gender: "",
    address: "",
    qualification: "",
    laptop: "",
    picture: null,
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      form.append(key, value);
    });
    try {
      const res = await axios.post("http://localhost:5000/api/register", form);
      alert("Form submitted successfully!");
    } catch (err) {
      alert("Submission failed!");
      console.error(err);
    }
  };

  return (
    <div className="form-container">
      <h2>Saylani Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="row">
          <select name="country" required onChange={handleChange}>
            <option value="">Select country</option>
            <option value="Pakistan">Pakistan</option>
          </select>

          <select name="city" required onChange={handleChange}>
            <option value="">Select city</option>
            <option value="Karachi">Karachi</option>
          </select>
        </div>

        <div className="row">
          <select name="course" required onChange={handleChange}>
            <option value="">Select course or event</option>
            <option value="Web Development">Web Development</option>
          </select>

          <select name="proficiency" required onChange={handleChange}>
            <option value="">Select computer proficiency</option>
            <option value="Beginner">Beginner</option>
            <option value="Intermediate">Intermediate</option>
            <option value="Expert">Expert</option>
          </select>
        </div>

        <div className="row">
          <input type="text" name="fullName" placeholder="Full name" required onChange={handleChange} />
          <input type="text" name="fatherName" placeholder="Father name" required onChange={handleChange} />
        </div>

        <div className="row">
          <input type="email" name="email" placeholder="Email" required onChange={handleChange} />
          <input type="tel" name="phone" placeholder="Phone" required onChange={handleChange} />
        </div>

        <div className="row">
          <input type="text" name="cnic" placeholder="CNIC" required onChange={handleChange} />
          <input type="text" name="fatherCnic" placeholder="Father's CNIC (optional)" onChange={handleChange} />
        </div>

        <div className="row">
          <input type="date" name="dob" required onChange={handleChange} />
          <select name="gender" required onChange={handleChange}>
            <option value="">Select gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>

        <input type="text" name="address" placeholder="Address" required onChange={handleChange} />
        <input type="text" name="qualification" placeholder="Last Qualification" required onChange={handleChange} />

        <select name="laptop" required onChange={handleChange}>
          <option value="">Do you have a Laptop?</option>
          <option value="Yes">Yes</option>
          <option value="No">No</option>
        </select>

        <div className="file-upload">
          <label>Upload Picture:</label>
          <input type="file" name="picture" accept="image/*" required onChange={handleChange} />
          <small>Max size 1MB, JPG/PNG, white/blue background, clear face</small>
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
